import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Trans } from 'react-i18next';
import { Form } from 'react-bootstrap';
import { userListUrl, token } from '../constant';

export class Rigth extends Component {
    constructor(props) {
        super(props);

        this.state = {
            users: [],
            DataisLoaded: false
        };

    }

    // ComponentDidMount is used to
    // execute the code 
    componentDidMount() {
        // let token = localStorage.getItem("token");

        fetch(
            userListUrl,{
                method: 'get',
                headers: {
                    'Content-Type' : 'application/json',  
                    'Authorization' :'Bearer '+ token
                }
            })
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    users: json,
                    DataisLoaded: true
                });
            })
            .catch((err) => {
                console.log("err:? " + err);
            })

    }
    render() {

        const { DataisLoaded, users } = this.state;
        if (!DataisLoaded) return <div>
            <h1> Pleses wait some time.... </h1> </div>;

        return (
            <>
                <div class="row d-flex justify-content-between ml-1 mr-3 mb-3">
                    <h2>Manage Rigths</h2>
                    <div class="row justify-content-around">
                        <Form.Group>
                            <div className="input-group">
                                <Form.Control type="text" className="form-control" placeholder="Search user" aria-label="Recipient's username" aria-describedby="basic-addon2" />
                                <div className="input-group-append">
                                    <button className="btn btn-sm btn-gradient-primary" type="button">Search</button>
                                </div>
                                <Link to="/user-manage/add">
                                    <button type="button" className="btn btn-gradient-primary btn-lg">+ Add User</button>
                                </Link>
                            </div>
                        </Form.Group>
                    </div>
                </div>

                <div className="row">
                    <div className="col-lg-12 grid-margin stretch-card">
                        <div className="card">
                            <div className="card-body">
                                <div className="table-responsive">
                                    <table className="table table-striped">
                                        <thead>
                                            <tr>
                                                <th> Email </th>
                                                <th> Created time </th>
                                                <th> Role </th>
                                                <th> Edit rights </th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {/* {table_content()} */}
                                            {
                                                users.map((user) => (
                                                    <tr>
                                                        <td> {user.email} </td>
                                                        <td>
                                                            {user.createdTime}
                                                        </td>
                                                        <td> {user.role.name} </td>
                                                        <td >
                                                            <Link to="/editrigth">
                                                                <Trans>
                                                                    <button type="button" class="btn btn-outline-primary btn-icon">
                                                                        <i class="mdi mdi-pencil"></i>
                                                                    </button>
                                                                </Trans>
                                                            </Link>
                                                        </td>
                                                    </tr>
                                                ))
                                            }
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>

        )
    }
}

export default Rigth;
