import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import { Form } from 'react-bootstrap';
import { loginUrl } from '../constant';
import axios from 'axios';
import { setUserSession } from '../common';

const Login = () => {
    const initialValues = { email: "", password: "" };
    const [formValues, setFormValues] = useState(initialValues);
    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);
    const [isReadyToRedirect, setIsReadyToRedirect] = useState(false);


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues({...formValues, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);

        console.log("formValues 26: " + JSON.stringify(formValues));

        axios
            .post(loginUrl, { //body
                "email": formValues.email,
                "password": formValues.password
            }, { //header
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            })
            .then(response => {
                setUserSession(response.data.token, response.data.user);
                // props.history.push('/dashboard');
                console.log("token: " + response.data.token)
            })
            .catch((error) => {
                if (error.response) {
                    console.log(error.response);
                    console.log("server responded");
                } else if (error.request) {
                    console.log("network error");
                } else {
                    console.log(error);
                }
            });
        // .catch(error => {
        //   console.log(error.code + " error status: " + error.status)
        //   console.log("error message: " + error.message)
        //   // if (error.response.status === 401) setFormErrors(error.response.data.message);
        //   // else setFormErrors("Something went wrong. Please try again later.");
        // });
        // fetch(loginUrl, {
        //   method: 'POST',
        //   headers: {
        //     'Content-Type': 'application/json',
        //     'Accept': 'application/json'
        //   },
        //   body: JSON.stringify(formValues),
        // })
        //   .then(response => {
        //     console.log("response token: "+response.token)

        //     if (!response.ok) {
        //       throw new Error(response.statusText)
        //     }

        //     return response.json()
        //   }).catch(err => {
        //     console.log("error: "+err)
        //   })
        // .then(result => {
        //   console.log("result status:" + result.status);
        //   console.log("token 38: " + result.token);
        // }
        // )

        // result =  await  result.json();
        // console.log("token 38: "+result.token);
        // localStorage.setItem('token',result.token)
    };


    useEffect(() => {
        console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
            console.log("formValues 46:" + formValues);
        }
    }, [formErrors]);

    const validate = (values) => {
        const errors = {};
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

        if (!values.email) {
            errors.email = "Email is required!";
        } else if (!regex.test(values.email)) {
            errors.email = "This is not a valid email format!";
        }
        if (!values.password) {
            errors.password = "Password is required";
        }
        return errors;
    };

    return ( <
        >
        <
        div className = "d-flex align-items-center auth px-0" >
        <
        div className = "row w-100 mx-0" >
        <
        div className = "col-lg-5 mx-auto" >
        <
        div className = "auth-form-light text-left py-5 px-4 px-sm-5" >
        <
        h4 > Hello!
        let 's get started</h4>

        <
        Form onSubmit = { handleSubmit }
        className = "pt-3" >

        <
        Form.Group className = "d-flex search-field" >
        <
        Form.Control type = "email"
        name = "email"
        value = { formValues.email }
        onChange = { handleChange }
        placeholder = "Email"
        size = "lg"
        className = "h-auto" /
        >
        <
        /Form.Group>

        <
        Form.Group className = "d-flex search-field" >
        <
        Form.Control type = "password"
        name = "password"
        value = { formValues.password }
        onChange = { handleChange }
        placeholder = "Password"
        size = "lg"
        className = "h-auto" /
        >
        <
        /Form.Group>

        <
        div className = "mt-3" >
        <
        button type = "submit"
        className = "btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn" >
        log in
        <
        /button> <
        /div>

        <
        div className = "mt-5" >
        <
        button type = "button"
        className = "btn btn-block btn-facebook auth-form-btn" >
        <
        i className = "mdi mdi-facebook mr-2" > < /i>Connect using facebook <
        /button> <
        button type = "button"
        className = "btn btn-block btn-google auth-form-btn" >
        <
        i className = "mdi mdi-google mr-2" > < /i>Connect using google <
        /button> <
        /div>

        <
        /Form> <
        /div> <
        /div> <
        /div> <
        /div> { isReadyToRedirect ? < Redirect from = "/login"
            push to = "/dashboard" / >: null } <
        />

    )

}

export default Login;
