import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import { Form } from 'react-bootstrap';
import { addUserUrl, token } from "../constant";

const AddUser = () => {
    const initialValues = {
        email: "",
        password: "",
        rePassword: "",
        roleId: ""
    };

    const [formValues, setFormValues] = useState(initialValues);
    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);
    // const [isReadyToRedirect, setIsReadyToRedirect] = useState(false);


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues({ ...formValues, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);

        alert("frm values: " + JSON.stringify(formValues));

        fetch(
            addUserUrl,{
                method: "POST",
                headers: {
                    'Content-Type' : 'application/json',  
                    'Authorization' :'Bearer '+ token
                },
                body: JSON.stringify(formValues)
            })
            .then((res) => res.json())
            .then((json) => {
                console.log(json)
            })
            .catch((err) => {
                console.log("err:? "+err);
            })
    };

    useEffect(() => {
        console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
            console.log(formValues);
        }
    }, [formErrors]);

    const validate = (values) => {
        const errors = {};
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

        if (!values.email) {
            errors.email = "Email is required!";
        } else if (!regex.test(values.email)) {
            errors.email = "This is not a valid email format!";
        }
        if (!values.password) {
            errors.password = "Password is required";
        } else if (values.password.length < 4) {
            errors.password = "Password must be more than 4 characters";
        } else if (values.password.length > 10) {
            errors.password = "Password cannot exceed more than 10 characters";
        }
        return errors;
    };

    return (
        <>
            <div class=" card ">
                <div class=" card-body row">
                    <div>
                        <p> <Link to="/rigth" class="text-muted">Manage Rights</Link> / Add user </p>
                    </div>
                </div>
            </div>

            <div className="card mt-3">
                <div className="card-body">

                    <form
                        onSubmit={handleSubmit}
                        className="form-sample"
                    >
                        <div className="row">
                            <div className="col-md-6">
                                <Form.Group className="row">
                                    <label className="col-sm-3 col-form-label">Email</label>
                                    <div className="col-sm-9">
                                        <Form.Control
                                            type="text"
                                            name="email"
                                            value={formValues.email}
                                            onChange={handleChange}
                                        />
                                    </div>
                                </Form.Group>
                            </div>

                            <div className="col-md-6">
                                <Form.Group className="row">
                                    <label className="col-sm-3 col-form-label">Role</label>
                                    <div className="col-sm-9">
                                        <select
                                        name="roleId"
                                            value={formValues.roleId}
                                            onChange={handleChange}
                                            className="form-control"
                                        >
                                            <option value="1">User</option>
                                            <option value="2">Content Manager</option>
                                            <option value="3">Administrator</option>

                                        </select>
                                    </div>
                                </Form.Group>
                            </div>
                        </div>

                        <div className="row">
                            <div className="col-md-6">
                                <Form.Group className="row">
                                    <label className="col-sm-3 col-form-label">Password</label>
                                    <div className="col-sm-9">
                                        <Form.Control
                                            type="text"
                                            name="password"
                                            value={formValues.password}
                                            onChange={handleChange}
                                        />
                                    </div>
                                </Form.Group>
                            </div>
                            <div className="col-md-6">
                                <Form.Group className="row">
                                    <label className="col-sm-3 col-form-label">Repeat password</label>
                                    <div className="col-sm-9">
                                        <Form.Control
                                            type="text"
                                            name="rePassword"
                                            value={formValues.rePassword}
                                            onChange={handleChange}
                                        />
                                    </div>
                                </Form.Group>
                            </div>
                        </div>

                        <div className="row">
                            <div className="col-md-6">
                                <button
                                     type="submit"
                                     className="btn btn-gradient-primary btn-lg">Create User</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>

        </>
    );
}
// }

export default AddUser;
