import { React, useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import { Form } from 'react-bootstrap';


export default class AddUser  {


    render() {
        const initialValues = { 
            username: "", 
            email: "", 
            password: "" ,
            company: "",
            right: ""
        };
        const [formValues, setFormValues] = useState(initialValues);
        const [formErrors, setFormErrors] = useState({});
        const [isSubmit, setIsSubmit] = useState(false);

        const handleChange = (e) => {
          const { name, value } = e.target;
          setFormValues({ ...formValues, [name]: value });
        };

        const handleSubmit = (e) => {
          e.preventDefault();
          setFormErrors(validate(formValues));
          alert(initialValues.username);
          setIsSubmit(true);
        };

        useEffect(() => {
            console.log(formErrors);
            if (Object.keys(formErrors).length === 0 && isSubmit) {
              console.log(formValues);
            }
          }, [formErrors]);
          const validate = (values) => {
            const errors = {};
            const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
            if (!values.username) {
              errors.username = "Username is required!";
            }
            if (!values.email) {
              errors.email = "Email is required!";
            } else if (!regex.test(values.email)) {
              errors.email = "This is not a valid email format!";
            }
            if (!values.password) {
              errors.password = "Password is required";
            } else if (values.password.length < 4) {
              errors.password = "Password must be more than 4 characters";
            } else if (values.password.length > 10) {
              errors.password = "Password cannot exceed more than 10 characters";
            }
            return errors;
          };

        return (
            <>
                <div class=" card ">
                    <div class=" card-body row">
                        <div>
                            <p> <Link to="/rigth" class="text-muted">Manage Rights</Link> / Add user </p>
                        </div>
                    </div>
                </div>

                <div className="card mt-3">
                    <div className="card-body"> 
                        <form className="form-sample" onSubmit={handleSubmit()}>
                            <div className="row">
                                <div className="col-md-6">
                                    <Form.Group className="row">
                                        <label className="col-sm-3 col-form-label">Username</label>
                                        <div className="col-sm-9">
                                            <Form.Control 
                                            type="text" 
                                            value={this.initialValues.username}
                                            onChange={handleChange()}
                                            />
                                        </div>
                                    </Form.Group>
                                </div>
                                <div className="col-md-6">
                                    <Form.Group className="row">
                                        <label className="col-sm-3 col-form-label">Password</label>
                                        <div className="col-sm-9">
                                            <Form.Control type="text" />
                                        </div>
                                    </Form.Group>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-md-6">
                                    <Form.Group className="row">
                                        <label className="col-sm-3 col-form-label">Email</label>
                                        <div className="col-sm-9">
                                            <Form.Control type="email"
                                            />
                                        </div>
                                    </Form.Group>
                                </div>
                                <div className="col-md-6">
                                    <Form.Group className="row">
                                        <label className="col-sm-3 col-form-label">Company name</label>
                                        <div className="col-sm-9">
                                            <Form.Control type="text" />
                                        </div>
                                    </Form.Group>
                                </div>
                            </div>

                            <div className="row my-3">
                                <div className="col-md-6">
                                    <div class="form-group">
                                        <div class="form-check">
                                            <label class="form-check-label">
                                                <input type="radio" name="right" class="form-check-input" />
                                                <i class="input-helper"></i>Manager rights (product: read/add/edit/remove) (order: read)
                                            </label>
                                        </div>
                                        <div class="form-check">
                                            <label class="form-check-label">
                                                <input type="radio" name="right" class="form-check-input" />
                                                <i class="input-helper"></i>Admin rights (user: read/edit)
                                            </label>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-md-6">
                                    <button type="button" className="btn btn-gradient-primary btn-lg">Create User</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>

            </>

        )
    }
}
