import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import { Form } from 'react-bootstrap';


const AddUser  = () => {

    // constructor(props) {
    //     super(props);
    //     this.state = { username: '' };

    //     this.handleChange = this.handleChange.bind(this);
    //     this.handleSubmit = this.handleSubmit.bind(this);
    // }

    // handleChange(event) {
    //     this.setState({ value: event.target.username });
    // };

    // handleSubmit(event) {
    //     alert('Отправленное имя: ' + this.state.username);
    //     event.preventDefault();
    // };


    // render() {
    const [email, setEmail] =  useState('');
    const [password, setPassword] =  useState('');

    const [emailDirty, setEmailDirty] =  useState(false);
    const [passwordDirty, setPasswordDirty] =  useState(false);

    const [emailError, setEmailError] =  useState('Email is empty');
    const [passwordError, setPasswordError] =  useState('Password is empty');

    const [formValid, setFormValid] =  useState(false);


    useEffect(() => {
        if (emailError || passwordError){
            setFormValid(false);
        }else{
            setFormValid(true);
        }
    }, [emailError,passwordError]);


    const emailHandler = (e) => {
        setEmail(e.target.value)
    };

    const passwordHandler = (e) => {
        setPassword(e.target.value)
        if(e.target.value.length < 8){
            setPasswordError("there password need to have more than 8 symbols")

        }else if(e.target.value.length == 0){
            setPasswordError("Password is empty")

        }
        else{
            setPasswordError("")
        }
    };

        return (
            <>
                <div class=" card ">
                    <div class=" card-body row">
                        <div>
                            <p> <Link to="/rigth" class="text-muted">Manage Rights</Link> / Add user </p>
                        </div>
                    </div>
                </div>

                <div className="card mt-3">
                    <div className="card-body">
                        <form className="form-sample" onSubmit={this.handleSubmit}>
                            <div className="row">
                                <div className="col-md-6">
                                    <Form.Group className="row">
                                        <label className="col-sm-3 col-form-label">Email</label>
                                        <div className="col-sm-9">
                                            {(emailDirty && emailError) && <p>{emailError}</p>}
                                            <input type="email"
                                                value={email}
                                                onChange={e => emailHandler(e)}
                                                />
                                            {/* <Form.Control
                                                type="text"
                                                value={this.state.username}
                                                onChange={this.handleChange}
                                            /> */}
                                        </div>
                                    </Form.Group>
                                </div>
                                <div className="col-md-6">
                                    <Form.Group className="row">
                                        <label className="col-sm-3 col-form-label">Password</label>
                                        <div className="col-sm-9">
                                            {(passwordDirty && passwordError) && <p>{passwordError}</p>}
                                            <input type="password"
                                                value={password}
                                                onChange={e => passwordHandler(e)}
                                                />
                                            {/* <Form.Control type="text" /> */}
                                        </div>
                                    </Form.Group>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-md-6">
                                    <Form.Group className="row">
                                        <label className="col-sm-3 col-form-label">Email</label>
                                        <div className="col-sm-9">
                                            <Form.Control type="email"
                                            />
                                        </div>
                                    </Form.Group>
                                </div>
                                <div className="col-md-6">
                                    <Form.Group className="row">
                                        <label className="col-sm-3 col-form-label">Company name</label>
                                        <div className="col-sm-9">
                                            <Form.Control type="text" />
                                        </div>
                                    </Form.Group>
                                </div>
                            </div>

                            <div className="row my-3">
                                <div className="col-md-6">
                                    <div class="form-group">
                                        <div class="form-check">
                                            <label class="form-check-label">
                                                <input type="radio" name="right" class="form-check-input" />
                                                <i class="input-helper"></i>Manager rights (product: read/add/edit/remove) (order: read)
                                            </label>
                                        </div>
                                        <div class="form-check">
                                            <label class="form-check-label">
                                                <input type="radio" name="right" class="form-check-input" />
                                                <i class="input-helper"></i>Admin rights (user: read/edit)
                                            </label>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-md-6">
                                    <button disabled={!formValid}
                                    type="button" className="btn btn-gradient-primary btn-lg">Create User</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>

            </>
        );
    }
// }

export default AddUser;
