import React, { Component, useState, useEffect } from 'react';
import { Form } from 'react-bootstrap';
// import { Editor } from "@tinymce/tinymce-react";
import { Link, Redirect } from 'react-router-dom';
import {  addPromoCardUrl } from '../constant';

const AddProduct = () => {

    const initialValues = {
        description: "",
        promoCodeCompanyId: ""
    };

    const [formValues, setFormValues] = useState(initialValues);
    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);



    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues({ ...formValues, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);

        console.log("frm values: " + JSON.stringify(formValues));

        console.log("cookie token: "+localStorage.getItem("token"))
        addApi()
    };

    function addApi() {
        fetch(addPromoCardUrl, {
            method: 'POST',
            headers: {
                'Content-Type' : 'application/json', 
                'Accept' : 'application/json',
                // 'Cache-Control': 'no-cache',
                'Authorization' : `Bearer ${localStorage.getItem("token")}`
            },
            body: {
                "promoCodeCompanyId":  formValues.promoCodeCompany,
                "description": formValues.description
            }
        })
            .then(res => {
                console.log(res.url)
                // document.location = res.url;
            })
            .then(success => {
                console.log("success: " + JSON.stringify(success));
                return <Redirect to="/all-product" />;
            })
            .catch(error => console.log("error: " + error));

    }

    useEffect(() => {
        console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
            console.log(formValues);
        }

    }, [formErrors]);

    const validate = (values) => {
        const errors = {};

        if (!values.promoCodeCompanyId) {
            errors.email = "promoCode CompanyId is required!";
        }

        // if (!values.password) {
        //     errors.description = "description is required";
        // }
        return errors;
    };

    return (
        <>
            <div class=" card ">
                <div class=" card-body row">
                    <p>   <Link to="/all-product" class="text-muted">Products</Link> / Add new product</p>
                </div>
            </div>

            <div className="card mt-3">
                <div className="card-body">
                    <form onSubmit={handleSubmit} className="form-sample">
                        <div className="row">
                            <div className="col-md-6">
                                <Form.Group className="row">
                                    <label className="col-sm-3 col-form-label">Description</label>
                                    <div className="col-sm-9">
                                        <Form.Control
                                            name="description"
                                            value={formValues.description}
                                            onChange={handleChange}
                                            type="text"
                                        />
                                    </div>
                                </Form.Group>
                            </div>
                            <div className="col-md-6">
                                <Form.Group className="row">
                                    <label className="col-sm-3 col-form-label">Company id</label>
                                    <div className="col-sm-9">
                                        <Form.Control
                                            name="promoCodeCompanyId"
                                            value={formValues.promoCodeCompanyId}
                                            onChange={handleChange}
                                            type="number" />
                                    </div>
                                </Form.Group>
                            </div>
                        </div>

                        <div className="row">
                            <div className="col-md-6">
                                <button
                                    type="submit"
                                    className="btn btn-gradient-primary btn-lg">Create Product</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>

        </>

    )
}
// }

export default AddProduct;
