import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Form } from 'react-bootstrap';
import { editUserRoleUrl, token } from '../constant';

const EditRight = () => {
    const initialValues = {
        "roleId": 0,
        "userId": 0
    };

    const [formValues, setFormValues] = useState(initialValues);
    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);
    // const [isReadyToRedirect, setIsReadyToRedirect] = useState(false);


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues({ ...formValues, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setIsSubmit(true);

        alert("frm values: " + JSON.stringify(formValues));

        fetch(
            editUserRoleUrl, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + token
            },
            body: JSON.stringify(formValues)
        })
            .then((res) => res.json())
            .then((json) => {
                console.log(json)
            })
            .catch((err) => {
                console.log("err:? " + err);
            })
    };

    useEffect(() => {
        console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
            console.log(formValues);
        }
    }, [formErrors]);


    const styles = {
        image: {
            height: 70,
            marginRight: 20
        },

    };

    const user = {
        username: "User Fullname",
        id: " 13t77fvtf4 ",
        imageUrl: "https://images.unsplash.com/photo-1633332755192-727a05c4013d?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8dXNlcnxlbnwwfHwwfHw%3D&w=1000&q=80",
        company: "Company name"
    }

    return (
        <>
            <div class=" card ">
                <div class=" card-body row">
                    <div class="">
                        <img style={styles.image} src={user.imageUrl} alt="user icon" class="rounded-circle" />
                    </div>
                    <div>
                        <p> <Link to="/rigth" class="text-muted">Manage Rights</Link> / User details</p>
                        <h3>{user.username} / {user.company}</h3>
                    </div>
                </div>
            </div>

            <div className="card mt-3">
                <div className="card-body">
                    <h4 className="card-title">User Information</h4>
                    <form className="form-sample">
                        <p className="card-description"> id:  {user.id}  </p>
                        <div className="row"> 
                                <form
                                    onSubmit={handleSubmit}
                                    className="form-sample"
                                >
                                    <div className="row">
                                        <div className="col-md-6">
                                            <Form.Group className="row">
                                                <label className="col-sm-3 col-form-label">Email</label>
                                                <div className="col-sm-9">
                                                    <Form.Control
                                                        type="text"
                                                        name="email"
                                                        value={formValues.email}
                                                        onChange={handleChange}
                                                    />
                                                </div>
                                            </Form.Group>
                                        </div>

                                        <div className="col-md-6">
                                            <Form.Group className="row">
                                                <label className="col-sm-3 col-form-label">Role</label>
                                                <div className="col-sm-9">
                                                    <select
                                                        name="roleId"
                                                        value={formValues.roleId}
                                                        onChange={handleChange}
                                                        className="form-control"
                                                    >
                                                        <option value="1">User</option>
                                                        <option value="2">Content Manager</option>
                                                        <option value="3">Administrator</option>

                                                    </select>
                                                </div>
                                            </Form.Group>
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <Form.Group className="row">
                                                <label className="col-sm-3 col-form-label">Password</label>
                                                <div className="col-sm-9">
                                                    <Form.Control
                                                        type="text"
                                                        name="password"
                                                        value={formValues.password}
                                                        onChange={handleChange}
                                                    />
                                                </div>
                                            </Form.Group>
                                        </div>
                                        <div className="col-md-6">
                                            <Form.Group className="row">
                                                <label className="col-sm-3 col-form-label">Repeat password</label>
                                                <div className="col-sm-9">
                                                    <Form.Control
                                                        type="text"
                                                        name="rePassword"
                                                        value={formValues.rePassword}
                                                        onChange={handleChange}
                                                    />
                                                </div>
                                            </Form.Group>
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <button
                                                type="submit"
                                                className="btn btn-gradient-primary btn-lg">Create User</button>
                                        </div>
                                    </div>
                                </form>
                                <div class="form-group">
                                    <div class="form-check">
                                        <label class="form-check-label">
                                            <input type="checkbox" class="form-check-input" />
                                            <i class="input-helper"></i>Manager rights (product: read/add/edit/remove) (order: read)
                                        </label>
                                    </div>
                                    <div class="form-check">
                                        <label class="form-check-label">
                                            <input type="checkbox" class="form-check-input" />
                                            <i class="input-helper"></i>Admin rights (user: read/edit)
                                        </label>
                                    </div>

                            </div>
                        </div>
                    </form>
                </div>
            </div>

        </>

    )
}


export default EditRight;
