import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';
import { Form } from 'react-bootstrap';
import { loginUrl } from '../constant';
import axios from 'axios';
import Cookie from 'react-cookie'

const Login = () => {
  const initialValues = { email: "", password: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const [token, setToken] = useState("")
  const [isReadyToRedirect, setIsReadyToRedirect] = useState(false);


  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  // const signIn = useSignIn()
  const API = async () => {
    const data = await axios.post(loginUrl, formValues);
    const response = await JSON.stringify(data.headers.token);



     localStorage.setItem("token", response)
//  console.log("response: "+response)
//      console.log("before token: "+localStorage.getItem("token"))



  }
  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);

    console.log("formValues 26: " + JSON.stringify(formValues));

    API()
localStorage.setItem("api",API());
console.log("API: "+JSON.stringify(localStorage.getItem("api")));
    console.log("after API");
    console.log("after token: "+localStorage.getItem("token"))

  };

  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.password) {
      errors.password = "Password is required";
    }
    return errors;
  };

  return (
    <>
      <div className="d-flex align-items-center auth px-0">
        <div className="row w-100 mx-0">
          <div className="col-lg-5 mx-auto">
            <div className="auth-form-light text-left py-5 px-4 px-sm-5">
              <h4>Hello! let's get started</h4>

              <Form
                onSubmit={handleSubmit}
                className="pt-3">

                <Form.Group className="d-flex search-field">
                  <Form.Control
                    type="email"
                    name="email"
                    value={formValues.email}
                    onChange={handleChange}
                    placeholder="Email"
                    size="lg" className="h-auto"
                  />
                </Form.Group>

                <Form.Group className="d-flex search-field">
                  <Form.Control
                    type="password"
                    name="password"
                    value={formValues.password}
                    onChange={handleChange}
                    placeholder="Password"
                    size="lg" className="h-auto"
                  />
                </Form.Group>

                <div className="mt-3">
                  <button
                    type="submit"
                    className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn" >
                    log in
                  </button>
                </div>

                <div className="mt-5">
                  <button type="button" className="btn btn-block btn-facebook auth-form-btn">
                    <i className="mdi mdi-facebook mr-2"></i>Connect using facebook
                  </button>
                  <button type="button" className="btn btn-block btn-google auth-form-btn">
                    <i className="mdi mdi-google mr-2"></i>Connect using google
                  </button>
                </div>

              </Form>
            </div>
          </div>
        </div>
      </div>
      {isReadyToRedirect ? <Redirect from="/login" push to="/dashboard" /> : null}
    </>

  )

}

export default Login;
