import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import { Form } from 'react-bootstrap';
import { loginUrl } from '../constant';
import axios from 'axios';
import { setUserSession } from '../common';

const Login = () => {
  const initialValues = { email: "", password: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const [token, setToken] = useState("none token")
  const [isReadyToRedirect, setIsReadyToRedirect] = useState(false);


  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const signIn = useSignIn()

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);

    console.log("formValues 26: " + JSON.stringify(formValues));

    // fetchProducts();
    axios.post(loginUrl, formValues)
    .then((res)=>{
        if(res.status === 200){
            if(signIn({token: res.data.token, 
                       expiresIn:res.data.expiresIn,
                       tokenType: "Bearer",
                       authState: res.data.authUserState})){
                // Redirect or do-something
            }else {
                console.log("Pizda")
            }
        }
    })
  };


  const fetchProducts = () => {
    // axios
    //   .post(loginUrl, {
    //     "email": "akonstudy@gmail.com",
    //     "password": "Qwerty1234!"
    //   }, {
    //     'Content-Type': 'text/plain'
    //   })
    //   .then((res) => {
    //     console.log("axios res: " + res);
    //   })
    //   .catch((err) => {
    //     console.log("axios err: " + err);
    //   });

    axios({
      method: 'POST',
      url: loginUrl,
      withCredentials: false,
      params: {
        access_token: SECRET_TOKEN,
      },
    });
  };

  // const sendReq = () => {

  //   console.log("try to send req");

  //   const requestOptions = {
  //     method: 'POST',
  //     mode: 'no-cors',
  //     credentials: 'same-origin',
  //     headers: {
  //       'Accept': 'application/json',
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({
  //       "email": "akonstudy@gmail.com",
  //       "password": "Qwerty1234!"
  //     })
  //   };

  //   fetch(loginUrl, requestOptions)
  //     .then(function (response) {
  //       console.log(response);
  //       return response.json();
  //     }).catch(function (err) {
  //       console.log(err)
  //     })
  //     .then(data => {
  //       setToken(data.token);
  //       console.log("data token " + data.token)
  //     });

  //   console.log("token " + token)
  //   console.log("already had send req")
  // }

  useEffect(() => {
    // console.log(formErrors);
    // if (Object.keys(formErrors).length === 0 && isSubmit) {
    //   console.log("formValues 46:" + formValues);
    // }

  }, []);


  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.password) {
      errors.password = "Password is required";
    }
    return errors;
  };

  return (
    <>
      <div className="d-flex align-items-center auth px-0">
        <div className="row w-100 mx-0">
          <div className="col-lg-5 mx-auto">
            <div className="auth-form-light text-left py-5 px-4 px-sm-5">
              <h4>Hello! let's get started</h4>

              <Form
                onSubmit={handleSubmit}
                className="pt-3">

                <Form.Group className="d-flex search-field">
                  <Form.Control
                    type="email"
                    name="email"
                    value={formValues.email}
                    onChange={handleChange}
                    placeholder="Email"
                    size="lg" className="h-auto"
                  />
                </Form.Group>

                <Form.Group className="d-flex search-field">
                  <Form.Control
                    type="password"
                    name="password"
                    value={formValues.password}
                    onChange={handleChange}
                    placeholder="Password"
                    size="lg" className="h-auto"
                  />
                </Form.Group>

                <div className="mt-3">
                  <button
                    type="submit"
                    className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn" >
                    log in
                  </button>
                </div>

                <div className="mt-5">
                  <button type="button" className="btn btn-block btn-facebook auth-form-btn">
                    <i className="mdi mdi-facebook mr-2"></i>Connect using facebook
                  </button>
                  <button type="button" className="btn btn-block btn-google auth-form-btn">
                    <i className="mdi mdi-google mr-2"></i>Connect using google
                  </button>
                </div>

              </Form>
            </div>
          </div>
        </div>
      </div>
      {isReadyToRedirect ? <Redirect from="/login" push to="/dashboard" /> : null}
    </>

  )

}

export default Login;
