import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import { Form } from 'react-bootstrap';


const Login = () => {
  const initialValues = { email: "", password: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    alert("is validate: " + validate(formValues))
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);

      // alert("email: "+formValues.email)
      if (formValues.email === "admin@mail.com" && formValues.password === "123456789") {
        // return <Redirect to="/dashboard" />
      }
    }
  }, [formErrors]);

  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.password) {
      errors.password = "Password is required";
    } else if (values.password.length < 4) {
      errors.password = "Password must be more than 4 characters";
    } else if (values.password.length > 10) {
      errors.password = "Password cannot exceed more than 10 characters";
    }
    return errors;
  };

  return (
    <>
      <div className="d-flex align-items-center auth px-0">
        <div className="row w-100 mx-0">
          <div className="col-lg-5 mx-auto">
            <div className="auth-form-light text-left py-5 px-4 px-sm-5">
              <h4>Hello! let's get started</h4>

              <Form
                onSubmit={handleSubmit}
                className="pt-3">

                <Form.Group className="d-flex search-field">
                  <Form.Control
                    type="email"
                    name="email"
                    value={formValues.email}
                    onChange={handleChange}
                    placeholder="Email"
                    size="lg" className="h-auto"
                  />
                </Form.Group>

                <Form.Group className="d-flex search-field">
                  <Form.Control
                    type="password"
                    name="password"
                    value={formValues.password}
                    onChange={handleChange}
                    placeholder="Password"
                    size="lg" className="h-auto"
                  />
                </Form.Group>

                <div className="mt-3">
                  <button
                    type="submit"
                    className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn" >
                    log in
                  </button>
                </div>

                <div className="mt-5">
                  <button type="button" className="btn btn-block btn-facebook auth-form-btn">
                    <i className="mdi mdi-facebook mr-2"></i>Connect using facebook
                  </button>
                  <button type="button" className="btn btn-block btn-google auth-form-btn">
                    <i className="mdi mdi-google mr-2"></i>Connect using google
                  </button>
                </div>

              </Form>
            </div>
          </div>
        </div>
      </div>
    </>
  )

}

export default Login;
